package JavaCode;

public class DatatypeExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean bool = true;
		char c = 'A';
		byte b = 125;
		short s = 30000;
		int i = 30000;
		long l = 2893992832l;
		float f = 78.89f;
		double d = 5500000.87;

		System.out.println("Boolean variable: " + bool);
		System.out.println("Character variable: " + c);
		System.out.println("Byte variable: " + b);
		System.out.println("Short variable: " + s);
		System.out.println("Integer variable: " + i);
		System.out.println("Long variable: " + l);
		System.out.println("Float variable: " + f);
		System.out.println("Double variable: " + d);

	}

}
